DROP DATABASE if exists `database`;
CREATE DATABASE if not exists `database`;

USE `database`;

CREATE TABLE Permission
(
  ID INT NOT NULL AUTO_INCREMENT,
  Name VARCHAR(25) NOT NULL,
  PRIMARY KEY (ID)
);


CREATE TABLE VideoPrivacy
(
  ID INT NOT NULL AUTO_INCREMENT,
  Name VARCHAR(25) NOT NULL,
  PRIMARY KEY (ID)
);


CREATE TABLE User
(
  UID VARCHAR(255) NOT NULL,
  Name VARCHAR(255) NOT NULL DEFAULT 'user',
  Email VARCHAR(255) NOT NULL,
  Bdate DATE,
  Password VARCHAR(255) NOT NULL,
  PermissionID INT NOT NULL DEFAULT 2,
  ProfilePic VARCHAR(2083) NOT NULL DEFAULT 'images/user_profile/default_user.png',
  Note TEXT NOT NULL Default '',
  PRIMARY KEY (UID),
  FOREIGN KEY (PermissionID) REFERENCES Permission(ID)
);


CREATE TABLE Video
(
  VID INT NOT NULL AUTO_INCREMENT,
  Title VARCHAR(255) NOT NULL,
  Size INT NOT NULL DEFAULT 0, #0 if store youtube video
  Description TEXT NOT NULL,
  UploadTime TIMESTAMP NOT NULL Default CURRENT_TIMESTAMP,
  Thumbnail VARCHAR(512) NOT NULL,
  ModifiedTime TIMESTAMP NOT NULL Default CURRENT_TIMESTAMP,
  View INT NOT NULL DEFAULT 0,
  Url VARCHAR(2083) NOT NULL,
  UID VARCHAR(255) NOT NULL,
  PrivacyID INT NOT NULL default 1,
  PRIMARY KEY (VID),
  FOREIGN KEY (UID) REFERENCES User(UID),
  FOREIGN KEY (PrivacyID) REFERENCES VideoPrivacy(ID)
);

CREATE TABLE VideoComment
(
  CommentID INT NOT NULL AUTO_INCREMENT,
  Comment TEXT NOT NULL,
  CommentTime TIMESTAMP NOT NULL Default CURRENT_TIMESTAMP,
  VID INT NOT NULL,
  UID VARCHAR(255) NOT NULL,
  PRIMARY KEY (CommentID),
  FOREIGN KEY (VID) REFERENCES Video(VID),
  FOREIGN KEY (UID) REFERENCES User(UID)
);

CREATE TABLE Channel
(
  ChannelID INT NOT NULL AUTO_INCREMENT,
  ChannelName VARCHAR(255) NOT NULL,
  UID VARCHAR(255) NOT NULL,
  PRIMARY KEY (ChannelID),
  FOREIGN KEY (UID) REFERENCES User(UID)
);



CREATE TABLE Playlist
(
  PID INT NOT NULL AUTO_INCREMENT,
  PName VARCHAR(255) NOT NULL,
  UID VARCHAR(255) NOT NULL,
  PRIMARY KEY (PID),
  FOREIGN KEY (UID) REFERENCES User(UID)
);

CREATE TABLE Feature
(
  VID INT,
  FOREIGN KEY (VID) REFERENCES Video(VID)
);

CREATE TABLE Subscribe
(
  UID VARCHAR(255) NOT NULL,
  ChannelID INT NOT NULL,
  PRIMARY KEY (UID, ChannelID),
  FOREIGN KEY (UID) REFERENCES User(UID),
  FOREIGN KEY (ChannelID) REFERENCES Channel(ChannelID)
);

CREATE TABLE PlaylistVideo
(
  PVID INT NOT NULL AUTO_INCREMENT,
  VID INT NOT NULL,
  PID INT NOT NULL,
  PRIMARY KEY (PVID),
  FOREIGN KEY (VID) REFERENCES Video(VID),
  FOREIGN KEY (PID) REFERENCES Playlist(PID)
);

